import React from 'react';
import './form.css'
import { useState } from 'react';
import { useDispatch } from 'react-redux';
import { setLoginUser } from '../../features/loginUser';

const Form = () => {

    const dispatch = useDispatch();

    const [formData, setFormData] = useState({
        email: '',
        password: '',
        
    });
    const handleChange = (event) => {
        const { id, value, type, checked } = event.target;
        const newValue = type === 'checkbox' ? checked : value;
        

        setFormData(prevState => ({
            ...prevState,
            [id]: newValue
        }));
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
        console.log(formData); 
        const error = document.querySelector(".error")
        if (formData.email === "" && formData.password === ""){
            error.innerText  = "Missing email and password";
            return
        }
        // Vérification des données du formulaire
        
        // Si données OK on envoi au serveur
        fetch ('http://localhost:3001/api/v1/user/login', {
            method : 'POST',
            headers: {
                'Content-Type': 'application.Json',
            },
            body: JSON.stringify(formData),
        })
        .then(response => response.json())
        .then(data => {
            // Sauvegarde du token 
            const token = 'test'
            dispatch(setLoginUser(token))
        })

    }

    return (
        <section className='login_container'>
            <div className="background">
                <div className="modal">

                        <i class="fa fa-user-circle sign-in-icon"></i>
                        <h1 className='modal_title' >Sign In</h1>

                    <form onSubmit={handleSubmit}>

                        <div className="input_email">
                        <label for="email">Username</label>
                        <input type="email" id='email' onChange={handleChange}/>
                        </div><br />
                        
                        <div className="input_password">
                        <label for="password">Password</label>
                        <input type="password" id='password' onChange={handleChange}/>
                        </div><br />
                        
                        <div className="input_remember">
                        <input type="checkbox" id='checkbox' checked={formData.rememberMe} onChange={handleChange} />
                        <label For="checkbox">Remember me</label>
                        </div><br />
                        
                        <div className="signin">
                        <input type="submit" className='sign_in' value="Sign In" />
                        </div>
                        <p className="error"></p>
                    </form>
                </div>
            </div>
        </section>
    );
};

export default Form;
