import React, {useState, useEffect} from 'react';
import { useSelector, useDispatch } from 'react-redux';
import Header from '../../components/header';
import Footer from '../../components/footer';
import { setProfile } from '../../features/userSlice';
import './user.css'

const Profile = () => {
    
    const dispatch = useDispatch();

    const user = useSelector(state => state.user);

    const [isLoading, setIsLoading] = useState(true);

    useEffect(() => {
        if(!user.token) return;

        fetch ('http://localhost:3001/api/v1/user/profile', {
            method : 'POST',
            headers: {
                'Content-Type': 'application/Json',
                'Authorization' : `Bearer ${user.token}`
            },
        })
        .then(response => {
            if(response.status !== 200){
                throw new Error('aie')
            }
            return response.json()
        })
        .then(data => {
            dispatch(setProfile({
                email : data.body.email,
                firstname: data.body.firstName,
                lastname: data.body.lastName
            }))
            setIsLoading(false)
        })
        .catch(err => {
            // redirection vers la page connexion
            console.log('redirection vers login')
        })

    }, [user.token])


    if(isLoading) return <p>chargement en cours...</p>
    
    return (
        <div className='user_page' >
            <Header />
            <main className='background_dark' >
                <div className="edit_name_banner">
                    <h1 className="edit_name_title">
                        Welcom back
                    </h1>
                    <button className="edit_name_btn">
                        Edit Name
                    </button>
                </div>
                    <section className="account">
                        <div className="account_content_wrapper">
                            <h3 className="acount_title">
                                Argent Bank Checking (x8349)
                            </h3>
                            <p className="account_amout">
                                $2,082.79
                            </p>
                            <p className="account_description">
                                Available Balance
                            </p>
                        </div>
                            <button className="transaction_btn">
                                View transaction
                            </button>
                        </section>
                    <section className="account">
                        <div className="account_content_wrapper">
                            <h3 className="acount_title">
                                Argent Bank Savings (x6712)
                            </h3>
                            <p className="account_amout">
                                $10,928.42
                            </p>
                            <p className="account_description">
                                Available Balance
                            </p>
                        </div>
                            <button className="transaction_btn">
                                View transaction
                            </button>
                        </section>
                    <section className="account">
                        <div className="account_content_wrapper">
                            <h3 className="acount_title">
                                Argent Bank Credit Card (x8349)
                            </h3>
                            <p className="account_amout">
                                $184.30
                            </p>
                            <p className="account_description">
                                Available Balance
                            </p>
                        </div>
                            <button className="transaction_btn">
                                View transaction
                            </button>
                        </section>
                
            </main>
            <Footer />
        </div>
    );
};

export default Profile;
